import React, { useEffect, useState } from "react";
import API from "../axiosInstance";

export default function Dashboard() {
  const [groceries, setGroceries] = useState([]);
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [editingId, setEditingId] = useState(null);

  // Fetch groceries from backend
  const fetchGroceries = () => {
    API.get("/groceries")
      .then((res) => setGroceries(res.data))
      .catch((err) => console.error("Error fetching groceries:", err));
  };

  useEffect(() => {
    fetchGroceries();
  }, []);

  // Add or update grocery
  const handleSubmit = (e) => {
    e.preventDefault();
    const groceryData = { name, quantity };

    if (editingId) {
      API.put(`/groceries/${editingId}`, groceryData)
        .then(() => {
          setEditingId(null);
          setName("");
          setQuantity("");
          fetchGroceries();
        })
        .catch((err) => console.error("Error updating grocery:", err));
    } else {
      API.post("/groceries", groceryData)
        .then(() => {
          setName("");
          setQuantity("");
          fetchGroceries();
        })
        .catch((err) => console.error("Error adding grocery:", err));
    }
  };

  // Edit grocery
  const handleEdit = (item) => {
    setEditingId(item._id);
    setName(item.name);
    setQuantity(item.quantity);
  };

  // Delete grocery
  const handleDelete = (id) => {
    API.delete(`/groceries/${id}`)
      .then(() => fetchGroceries())
      .catch((err) => console.error("Error deleting grocery:", err));
  };

  return (
    <div className="max-w-2xl mx-auto p-6">
      {/* Header */}
      <h1 className="text-3xl font-bold mb-6 text-center">
        ðŸ›’ Grocery Manager
      </h1>

      {/* Add/Edit Form */}
      <form
        onSubmit={handleSubmit}
        className="flex gap-2 mb-6 bg-white p-4 shadow rounded"
      >
        <input
          type="text"
          placeholder="Grocery Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="border p-2 flex-1 rounded"
          required
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          className="border p-2 w-28 rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          {editingId ? "Update" : "Add"}
        </button>
      </form>

      {/* Grocery List */}
      <ul className="bg-white shadow rounded divide-y">
        {groceries.length > 0 ? (
          groceries.map((item) => (
            <li
              key={item._id}
              className="flex justify-between items-center p-3"
            >
              <span>
                {item.name} â€” <strong>{item.quantity}</strong>
              </span>
              <div className="flex gap-2">
                <button
                  onClick={() => handleEdit(item)}
                  className="px-3 py-1 text-sm bg-yellow-400 text-white rounded hover:bg-yellow-500"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(item._id)}
                  className="px-3 py-1 text-sm bg-red-500 text-white rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </div>
            </li>
          ))
        ) : (
          <li className="p-3 text-gray-500 text-center">No groceries found</li>
        )}
      </ul>
    </div>
  );
}
