import React, { useEffect, useState } from "react";
import axios from "axios";

function Home() {
  const [groceries, setGroceries] = useState([]);

  // Fetch grocery items from backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/groceries")
      .then((res) => setGroceries(res.data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen">
      {/* Navbar */}
      <nav className="bg-green-600 text-white p-4 shadow">
        <div className="max-w-6xl mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">ðŸ›’ Grocery Manager</h1>
          <ul className="flex gap-6">
            <li><a href="/" className="hover:text-gray-200">Home</a></li>
            <li><a href="/dashboard" className="hover:text-gray-200">Dashboard</a></li>
          </ul>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="bg-green-100 py-12">
        <div className="max-w-6xl mx-auto text-center">
          <h2 className="text-4xl font-bold mb-4">Fresh Groceries Delivered to You</h2>
          <p className="text-lg text-gray-700 mb-6">
            Shop fresh vegetables, fruits, and essentials at the best prices.
          </p>
          <a
            href="/dashboard"
            className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700"
          >
            Shop Now
          </a>
        </div>
      </section>

      {/* Product Grid */}
      <section className="max-w-6xl mx-auto py-12">
        <h3 className="text-2xl font-bold mb-6 text-center">Popular Products</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {groceries.length > 0 ? (
            groceries.map((item) => (
              <div key={item._id} className="bg-white shadow rounded-lg p-4">
                <img
                  src={item.imageUrl || "https://via.placeholder.com/150"}
                  alt={item.name}
                  className="w-full h-40 object-cover rounded"
                />
                <h4 className="mt-4 font-bold">{item.name}</h4>
                <p className="text-green-600 font-semibold">â‚¹{item.price}</p>
                <button className="mt-2 w-full bg-green-600 text-white py-2 rounded hover:bg-green-700">
                  Add to Cart
                </button>
              </div>
            ))
          ) : (
            <p className="text-center col-span-4">No products found.</p>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-green-600 text-white py-4 text-center">
        Â© 2025 Grocery Manager. All rights reserved.
      </footer>
    </div>
  );
}

export default Home;
